settings.custom_liquid:
{% comment %}
  Custom Liquid Block: Product Arrival Dates (Compact & Boxed, Show 1 Date)

  Paste this entire code block into a "Custom Liquid" block
  within your product information section in the theme editor.

  Requirements:
  1. The associated JavaScript ('product-arrival-dates.js' or similar) MUST be loaded separately by your theme.
  2. The Gamma API Token MUST be set correctly in Theme Settings -> API Integrations (settings.gamma_api_token).
  3. Product variants MUST have SKUs assigned.

  Note: The availability of Liquid variables like 'current_variant' inside the <script>
  tag depends on the context where this Custom Liquid block is placed.
  The JavaScript has fallbacks, but ensure 'settings.gamma_api_token' is accessible.
{% endcomment %}

{% comment %} --- HTML Structure --- {% endcomment %}
<div class="product-arrival-section-wrapper">
  {% comment %} Check if API token is set - prevent rendering if not {% endcomment %}
  {%- if settings.gamma_api_token != blank -%}
    {% comment %} Added 'boxed' class for styling {% endcomment %}
    <div id="product-arrival-dates"
         class="product-arrival-dates-container product-arrival-dates--compact boxed" {% comment %} Defaulting to compact layout class {% endcomment %}
         style="display: none;" {% comment %} Initially hidden, JS will show it {% endcomment %}>

      {% comment %} Loading State Indicator {% endcomment %}
      <div id="arrival-dates-loading" class="arrival-dates-state arrival-dates-loading" style="display: none; justify-content: center; align-items: center; min-height: 40px; color: #555;">
        <div class="lds-dual-ring"></div>
        <span style="margin-left: 10px;">Loading dates...</span>
      </div>

      {% comment %} Error State Message {% endcomment %}
      <div id="arrival-dates-error" class="arrival-dates-state arrival-dates-error" style="display: none; color: #d9534f; padding: 10px;">
        Error loading arrival dates.
      </div>

      {% comment %} No Data State Message {% endcomment %}
      <div id="arrival-dates-no-data" class="arrival-dates-state arrival-dates-no-data" style="display: none; color: #777; padding: 10px;">
        No estimated arrival dates available.
      </div>

      {% comment %} Content Area - Populated by JavaScript {% endcomment %}
      <div id="arrival-dates-content" class="arrival-dates-content" style="display: none;">
        {% comment %} JavaScript will render content here {% endcomment %}
      </div>
    </div>
  {%- else -%}
     <div class="product-arrival-dates-disabled" style="padding: 10px; color: #777; font-style: italic; font-size: 0.9em;">
       {% comment %} Message shown if API token is missing in theme settings {% endcomment %}
       Arrival dates require API Token configuration.
     </div>
  {%- endif -%}
</div>

{% comment %} --- JavaScript Configuration --- {% endcomment %}
{% comment %} This script block needs Liquid variables to be processed correctly {% endcomment %}
{%- if settings.gamma_api_token != blank -%}
  {% comment %} Assign required variables - ensure 'product' and 'current_variant' are available in this context {% endcomment %}
  {%- liquid
    assign current_product = product | default: product
    assign current_variant = current_product.selected_or_first_available_variant
    assign api_token = settings.gamma_api_token
    assign api_base_url = settings.gamma_api_base_url | default: "https://api.gammapowersports.com"

    # Default settings (since block has no schema) - Adjust as needed
    assign force_check = false
    assign use_compact_layout = true
    # --- MODIFIED: Max visible set to 1 ---
    assign max_visible_compact = 1
    assign title_standard = "Estimated Arrival Dates"
    assign note_standard = "<strong>Note:</strong> Confirmed dates are based on shipping information, while estimated dates are based on purchase orders and subject to change."
    assign title_compact = "Expected Arrival:"
    assign note_compact = "Confirmed = In Transit, Estimated = On Order. Dates subject to change."
    assign label_cargo = "Confirmed"
    assign label_po = "Estimated"
    assign label_show_more = "Show {count} more date{s}"
    assign label_show_less = "Hide extra dates"
  -%}

  <script type="text/javascript">
    // Ensure config setup runs only once per page load if multiple blocks exist
    if (typeof window.arrivalDatesConfig === 'undefined') {
      window.arrivalDatesConfig = {
        // API Details
        authToken: {{ api_token | json }},
        apiUrl: {{ api_base_url | json }},

        // Product Details (Initial State) - Relies on 'current_variant' being available here
        productSku: {{ current_variant.sku | json }},
        productInStock: {{ current_variant.available | json }},

        // Behavior Settings (Hardcoded defaults for Custom Liquid block)
        forceCheck: {{ force_check | json }},
        useCompactLayout: {{ use_compact_layout | json }},
        // --- MODIFIED: Max visible set to 1 ---
        maxVisibleCompact: {{ max_visible_compact | json }},

        // Labels & Text (Hardcoded defaults)
        titleStandard: {{ title_standard | json }},
        noteStandard: {{ note_standard | json }},
        titleCompact: {{ title_compact | json }},
        noteCompact: {{ note_compact | json }},
        labelCargo: {{ label_cargo | json }},
        labelPO: {{ label_po | json }},
        labelShowMore: {{ label_show_more | json }},
        labelShowLess: {{ label_show_less | json }},

        // Diagnostics (Keep false unless debugging)
        enableApiDiagnostics: false,
        skipSanitization: false
      };

      // Optional: Trigger initialization if needed by your JS file
      // document.dispatchEvent(new CustomEvent('productArrivalDates:configLoaded'));
    } else {
      // Config already exists, maybe update SKU if possible?
      console.log('Product Arrival Dates: window.arrivalDatesConfig already exists.');
    }
  </script>
{%- endif -%}


{% comment %} --- Basic CSS --- {% endcomment %}
{% comment %} You might want to move this to your theme's main CSS file {% endcomment %}
<style>
  /* --- Style for the Box --- */
  .product-arrival-section-wrapper .product-arrival-dates-container.boxed {
    border: 1px solid #e0e0e0; /* Light grey border */
    padding: 15px;             /* Padding inside the box */
    border-radius: 5px;        /* Slightly rounded corners */
    margin-top: 15px;          /* Space above the box */
    margin-bottom: 15px;       /* Space below the box */
    background-color: #f9f9f9; /* Optional: slight background color */
  }
  /* Adjust error message style within the box */
   .product-arrival-section-wrapper .product-arrival-dates-container.boxed .arrival-dates-error {
      border: none;
      background-color: #fdecea; /* Light red background for error */
      color: #932c2c;
   }

  /* Loader */
  .product-arrival-section-wrapper .lds-dual-ring {
    display: inline-block;
    width: 20px;
    height: 20px;
  }
  .product-arrival-section-wrapper .lds-dual-ring:after {
    content: " ";
    display: block;
    width: 16px;
    height: 16px;
    margin: 2px;
    border-radius: 50%;
    border: 3px solid #555;
    border-color: #555 transparent #555 transparent;
    animation: lds-dual-ring 1.2s linear infinite;
  }
  @keyframes lds-dual-ring {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  /* Compact Layout Styles */
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__list { list-style: none; padding: 0; margin: 5px 0; }
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__item { display: flex; align-items: center; gap: 8px; padding: 3px 0; font-size: 0.9em; flex-wrap: wrap; }
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__date { font-weight: bold; margin-right: 5px;}
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__qty { color: #666; margin-right: 5px;}
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__status { flex-shrink: 0; }
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__status .confirmed { color: green; font-weight: bold; }
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__status .estimated { color: orange; }
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__toggle { background: none; border: none; color: var(--color-accent-1, #004FC7); text-decoration: underline; cursor: pointer; padding: 5px 0; font-size: 0.9em; display: block; margin-top: 5px; }
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__note { font-size: 0.85em; color: #666; margin-top: 8px; }
  .product-arrival-section-wrapper .product-arrival-dates--compact .arrival-dates-subtle__title { font-weight: bold; margin-bottom: 5px; font-size: 1em; }

  /* Hide standard layout styles if needed */
  .product-arrival-section-wrapper .product-arrival-dates--standard { display: none; }

</style>
